import React, { useEffect, useState } from 'react'
import { addDoc, collection, serverTimestamp, onSnapshot, query, where } from 'firebase/firestore'
import { db, auth } from '../config/firebase'

function ChatApp(props) {
  const {room} = props;
  const [message, setMessage] = useState("");
  const [sms, setSms] = useState([]);

  const messageRef = collection(db, 'messages');

  useEffect(() => {
    const queryMessages = query(messageRef, where("room", "==", room));
    const unsubscribe = onSnapshot(queryMessages, (snapshot) => {
      let messages = [];
      snapshot.forEach((doc) => {
        messages.push({...doc.data(), id: doc.id });
      });
      setSms(messages);
    });
    return () => unsubscribe();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (message === "") return;
    await addDoc(messageRef, {
      text: message,
      createAt: serverTimestamp(),
      user: auth.currentUser.displayName,
      room: room,
    });

    setMessage("");
  };

  return (
    <div>
      <div className='p-3 border border-slate-900'>
        <p className='text-3xl mb-7 text-center'>Welcome to Room: {room}</p>
        {sms.map(message => (
          <div>
            <div className='p-2 flex flex-row'>
              <p className='px-2'>{message.user} : </p>
              <p className='px-2'>{message.text}</p>
            </div>
          </div>
        ))}
      </div>
      <form>
        <input type="text" placeholder='Enter your message' value={message} onChange={e => setMessage(e.target.value)} />
        <button type='submit' onClick={handleSubmit }>Submit</button>
      </form>
    </div>
  )
}

export default ChatApp
